import React, { useMemo, useState } from 'react';
import { photos } from './data';

export default function App() {
  const [q, setQ] = useState('');

  const filtered = useMemo(() => {
    return photos.filter(p =>
      p.title.toLowerCase().includes(q.trim().toLowerCase())
    );
  }, [q]);

  return (
    <div className="app">
      <Header />
      <main className="main">
        <div className="watermark" aria-hidden="true"></div>
        <div className="toolbar">
          <input
            className="search"
            placeholder="Search location..."
            value={q}
            onChange={e => setQ(e.target.value)}
          />
        </div>
        <Gallery items={filtered} />
      </main>
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <div className="container header-inner">
        <h1 className="title glow">Photo Gallery</h1>
        <nav className="nav">
          <a href="#home">HOME</a>
          <a href="#list">LIST</a>
          <a href="#contact">CONTACT</a>
        </nav>
      </div>
    </header>
  );
}

function Gallery({ items }) {
  return (
    <section className="gallery container">
      {items.map(item => (
        <figure key={item.id} className="card">
          <div className="img-wrap">
            <img src={item.img} alt={item.title} loading="lazy" />
          </div>
          <figcaption className="caption">{item.title}</figcaption>
        </figure>
      ))}
    </section>
  );
}

function Footer() {
  return (
    <footer className="footer">
      <div className="container">
        Â© Akash Edits Photography - Shuvendu Ghosh
      </div>
    </footer>
  );
}
