import React, { useMemo, useState, useEffect } from 'react';
import { Search, MapPin, Download, Share2, X, ChevronLeft, ChevronRight, Moon, Sun, Play, Pause } from 'lucide-react';
import { photos } from './data';
import GoogleMap from './GoogleMap';

export default function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isGoldTheme, setIsGoldTheme] = useState(false);
  const [isSlideshow, setIsSlideshow] = useState(false);
  const [currentSlideIndex, setCurrentSlideIndex] = useState(0);

  const filteredPhotos = useMemo(() => {
    return photos.filter(photo => {
      const matchesSearch = photo.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        photo.location.toLowerCase().includes(searchQuery.toLowerCase()) ||
        photo.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesSearch;
    });
  }, [searchQuery]);

  // Slideshow functionality
  useEffect(() => {
    if (isSlideshow && selectedPhoto) {
      const interval = setInterval(() => {
        setCurrentSlideIndex(prev => (prev + 1) % filteredPhotos.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [isSlideshow, selectedPhoto, filteredPhotos.length]);

  useEffect(() => {
    if (selectedPhoto) {
      const photoIndex = filteredPhotos.findIndex(p => p.id === selectedPhoto.id);
      setCurrentSlideIndex(photoIndex);
    }
  }, [selectedPhoto, filteredPhotos]);

  const openLightbox = (photo) => {
    setSelectedPhoto(photo);
    document.body.style.overflow = 'hidden';
  };

  const closeLightbox = () => {
    setSelectedPhoto(null);
    setIsSlideshow(false);
    document.body.style.overflow = 'unset';
  };

  const navigatePhoto = (direction) => {
    const currentIndex = filteredPhotos.findIndex(p => p.id === selectedPhoto.id);
    let newIndex;

    if (direction === 'next') {
      newIndex = (currentIndex + 1) % filteredPhotos.length;
    } else {
      newIndex = currentIndex === 0 ? filteredPhotos.length - 1 : currentIndex - 1;
    }

    setSelectedPhoto(filteredPhotos[newIndex]);
    setCurrentSlideIndex(newIndex);
  };

  const downloadImage = async (imageUrl, filename) => {
    try {
      const response = await fetch(imageUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  const sharePhoto = (photo) => {
    if (navigator.share) {
      navigator.share({
        title: photo.title,
        text: photo.description,
        url: window.location.href
      });
    } else {
      // Fallback for browsers that don't support Web Share API
      const shareUrl = `https://wa.me/?text=${encodeURIComponent(`Check out this amazing Ganesh idol: ${photo.title} - ${photo.description}`)}`;
      window.open(shareUrl, '_blank');
    }
  };

  return (
    <div className={`app ${isGoldTheme ? 'gold-theme' : ''}`}>
      <Header
        isGoldTheme={isGoldTheme}
        setIsGoldTheme={setIsGoldTheme}
      />
      <main className="main">
        <div className="container">
          <Controls
            searchQuery={searchQuery}
            setSearchQuery={setSearchQuery}
          />
          <Gallery
            photos={filteredPhotos}
            onPhotoClick={openLightbox}
            onDownload={downloadImage}
            onShare={sharePhoto}
          />
          <MapSection
            photos={filteredPhotos}
            selectedPhoto={selectedPhoto}
            onPhotoSelect={openLightbox}
          />
        </div>
      </main>

      {selectedPhoto && (
        <Lightbox
          photo={selectedPhoto}
          photos={filteredPhotos}
          currentIndex={currentSlideIndex}
          onClose={closeLightbox}
          onNavigate={navigatePhoto}
          onDownload={downloadImage}
          onShare={sharePhoto}
          isSlideshow={isSlideshow}
          setIsSlideshow={setIsSlideshow}
        />
      )}

      <Footer />
    </div>
  );
}

function Header({ isGoldTheme, setIsGoldTheme }) {
  return (
    <header className="header">
      <div className="container header-inner">
        <h1 className="title glow">üïâÔ∏è Ganesh Gallery</h1>
        <nav className="nav">
          <a href="#home">HOME</a>
          <a href="#gallery">GALLERY</a>
          <a href="#map">MAP</a>
          <a href="#contact">CONTACT</a>
          <button
            className="theme-toggle"
            onClick={() => setIsGoldTheme(!isGoldTheme)}
            title={isGoldTheme ? 'Switch to Neon Theme' : 'Switch to Gold Theme'}
          >
            {isGoldTheme ? <Moon size={16} /> : <Sun size={16} />}
            {isGoldTheme ? 'Neon' : 'Gold'}
          </button>
        </nav>
      </div>
    </header>
  );
}

function Controls({ searchQuery, setSearchQuery }) {
  return (
    <section className="controls">
      <div className="search-container">
        <Search className="search-icon" size={20} />
        <input
          className="search"
          placeholder="Search locations, descriptions..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>
    </section>
  );
}

function Gallery({ photos, onPhotoClick, onDownload, onShare }) {
  if (photos.length === 0) {
    return (
      <div className="container">
        <div style={{ textAlign: 'center', padding: '4rem', color: 'var(--text-secondary)' }}>
          <h3>No photos found</h3>
          <p>Try adjusting your search or filter criteria</p>
        </div>
      </div>
    );
  }

  return (
    <section className="gallery container" id="gallery">
      {photos.map((photo, index) => (
        <PhotoCard
          key={photo.id}
          photo={photo}
          index={index}
          onClick={() => onPhotoClick(photo)}
          onDownload={() => onDownload(photo.img, `${photo.location}-ganesh.jpg`)}
          onShare={() => onShare(photo)}
        />
      ))}
    </section>
  );
}

function PhotoCard({ photo, index, onClick, onDownload, onShare }) {
  return (
    <div
      className="photo-card"
      style={{ animationDelay: `${index * 0.1}s` }}
      onClick={onClick}
    >
      <div className="img-container">
        <img
          src={photo.img}
          alt={photo.title}
          className="photo-img"
          loading="lazy"
        />
        <div className="card-overlay">
          <div className="card-actions">
            <button
              className="action-btn"
              onClick={(e) => {
                e.stopPropagation();
                onDownload();
              }}
              title="Download"
            >
              <Download size={20} />
            </button>
            <button
              className="action-btn"
              onClick={(e) => {
                e.stopPropagation();
                onShare();
              }}
              title="Share"
            >
              <Share2 size={20} />
            </button>
          </div>
        </div>
      </div>

      <div className="card-content">
        <h3 className="card-title">{photo.title}</h3>
        <div className="card-location">
          <MapPin size={16} />
          {photo.location}
        </div>

      </div>
    </div>
  );
}

function Lightbox({
  photo,
  photos,
  currentIndex,
  onClose,
  onNavigate,
  onDownload,
  onShare,
  isSlideshow,
  setIsSlideshow
}) {
  const currentPhoto = photos[currentIndex] || photo;

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowLeft') onNavigate('prev');
      if (e.key === 'ArrowRight') onNavigate('next');
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [onClose, onNavigate]);

  return (
    <div className="lightbox-overlay" onClick={onClose}>
      <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
        <button className="lightbox-close" onClick={onClose}>
          <X size={20} />
        </button>

        {photos.length > 1 && (
          <>
            <button className="lightbox-nav lightbox-prev" onClick={() => onNavigate('prev')}>
              <ChevronLeft size={24} />
            </button>
            <button className="lightbox-nav lightbox-next" onClick={() => onNavigate('next')}>
              <ChevronRight size={24} />
            </button>
          </>
        )}

        <img
          src={currentPhoto.img}
          alt={currentPhoto.title}
          className="lightbox-img"
        />

        <div className="lightbox-info">
          <h2 className="lightbox-title">{currentPhoto.title}</h2>
          <div className="card-location" style={{ marginBottom: '1rem' }}>
            <MapPin size={16} />
            {currentPhoto.location}
          </div>
          <p className="lightbox-description">{currentPhoto.description}</p>

          <div className="lightbox-actions">
            {photos.length > 1 && (
              <button
                className="lightbox-btn"
                onClick={() => setIsSlideshow(!isSlideshow)}
              >
                {isSlideshow ? <Pause size={16} /> : <Play size={16} />}
                {isSlideshow ? 'Pause' : 'Slideshow'}
              </button>
            )}
            <button
              className="lightbox-btn"
              onClick={() => onDownload(currentPhoto.img, `${currentPhoto.location}-ganesh.jpg`)}
            >
              <Download size={16} />
              Download
            </button>
            <button
              className="lightbox-btn"
              onClick={() => onShare(currentPhoto)}
            >
              <Share2 size={16} />
              Share
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

function MapSection({ photos }) {
  return (
    <section className="map-container" id="map">
      <div className="static-map">
        <div className="map-header">
          <h3 className="map-title">üìç Cuttack City - Ganesh Locations</h3>
          <p className="map-subtitle">Showing {photos.length} beautiful Ganesh idol locations</p>
        </div>

        <div className="map-content">
          <div className="city-map">
            <div className="map-background">
              <div className="location-marker" style={{top: '30%', left: '45%'}} data-location="Sheikh Bazar">
                <div className="marker-dot"></div>
                <span className="marker-label">Sheikh Bazar</span>
              </div>

              <div className="location-marker" style={{top: '40%', left: '55%'}} data-location="Stadium">
                <div className="marker-dot"></div>
                <span className="marker-label">Stadium</span>
              </div>

              <div className="location-marker" style={{top: '35%', left: '40%'}} data-location="Mohammedia Bazar">
                <div className="marker-dot"></div>
                <span className="marker-label">Mohammedia Bazar</span>
              </div>

              <div className="location-marker" style={{top: '45%', left: '42%'}} data-location="Chandi Mandir">
                <div className="marker-dot"></div>
                <span className="marker-label">Chandi Mandir</span>
              </div>

              <div className="location-marker" style={{top: '25%', left: '50%'}} data-location="Chandini Chhak">
                <div className="marker-dot"></div>
                <span className="marker-label">Chandini Chhak</span>
              </div>

              <div className="location-marker" style={{top: '20%', left: '60%'}} data-location="High Court">
                <div className="marker-dot"></div>
                <span className="marker-label">High Court</span>
              </div>

              <div className="location-marker" style={{top: '50%', left: '38%'}} data-location="Kazi Bazar">
                <div className="marker-dot"></div>
                <span className="marker-label">Kazi Bazar</span>
              </div>

              <div className="location-marker" style={{top: '55%', left: '35%'}} data-location="Keuta Sahi">
                <div className="marker-dot"></div>
                <span className="marker-label">Keuta Sahi</span>
              </div>

              <div className="location-marker" style={{top: '60%', left: '40%'}} data-location="Mali Sahi">
                <div className="marker-dot"></div>
                <span className="marker-label">Mali Sahi</span>
              </div>

              <div className="location-marker" style={{top: '15%', left: '65%'}} data-location="Mission Road">
                <div className="marker-dot"></div>
                <span className="marker-label">Mission Road</span>
              </div>

              <div className="location-marker" style={{top: '65%', left: '32%'}} data-location="Purighat">
                <div className="marker-dot"></div>
                <span className="marker-label">Purighat</span>
              </div>

              <div className="location-marker" style={{top: '10%', left: '70%'}} data-location="Ranihat">
                <div className="marker-dot"></div>
                <span className="marker-label">Ranihat</span>
              </div>

              {/* River representation */}
              <div className="river"></div>

              {/* City center */}
              <div className="city-center">
                <span>Cuttack City Center</span>
              </div>
            </div>
          </div>

          <div className="map-legend">
            <h4>üïâÔ∏è Festival Locations</h4>
            <div className="legend-items">
              {photos.map((photo, index) => (
                <div key={photo.id} className="legend-item">
                  <div className="legend-marker">{index + 1}</div>
                  <span>{photo.location}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

function Footer() {
  return (
    <footer className="footer">
      <div className="container">
        <p>¬© 2025 Akash Edits Photography - Shuvendu Ghosh</p>
        <p>Celebrating the divine beauty of Lord Ganesh across the city</p>
      </div>
    </footer>
